mapper.select:
select
  content
from dafa_cloud.bet_content
where  content_type =${contentType}
and lottery_code = ${lotteryCode}

mapper.insert:
insert into dafa_cloud.bet_content(
content,
content_type,
lottery_code
) values

    (
    #{item.content},
    #{item.contentType},
    #{item.lotteryCode}
    )
