mapper.select:
select id id ,api_name apiName ,description description ,path path ,method method ,request_parameters requestParameters ,request_header requestHeader ,cms_front cmsFront ,project project ,project_module projectModule ,sort sort ,owner owner ,assert_response assertResponse ,dependent_api dependentApi ,response_comments responseComments ,api_groups apiGroups
,assert_type assertType ,assert_content assertContent
from api_manage
AND api_name like CONCAT(CONCAT('%',#{apiName}),'%')
AND path like CONCAT(CONCAT('%',#{path}),'%')
AND owner = #{owner}
AND project_module = #{projectModule}
AND cms_front = #{cmsFront}
AND project = #{project}
AND api_groups like CONCAT(CONCAT('%',#{apiGroups}),'%')
AND sort like CONCAT(CONCAT(#{sort}),'%')
order by sort asc,id desc limit #{pageNum},#{pageSize}

select count(1) from api_manage


        AND api_name like CONCAT(CONCAT('%',#{apiName}),'%')


        AND path like CONCAT(CONCAT('%',#{path}),'%')


        AND owner = #{owner}


        AND project_module = #{projectModule}


        AND cms_front = #{cmsFront}


        AND project = #{project}


        AND api_groups like CONCAT(CONCAT('%',#{apiGroups}),'%')


        AND sort like CONCAT(CONCAT(#{sort}),'%')

mapper.update:
		update api_manage set
		api_name = #{apiName}
        ,description = #{description}
        ,path = #{path}
        ,method = #{method}
        ,request_parameters = #{requestParameters}
        ,request_header = #{requestHeader}
        ,cms_front = #{cmsFront}
        ,project = #{project}
        ,project_module = #{projectModule}
        ,sort = #{sort}
        ,owner = #{owner}
        ,assert_response = #{assertResponse}
        ,dependent_api = #{dependentApi}
        ,response_comments = #{responseComments}
        ,api_groups = #{apiGroups}
        ,assert_type =  #{assertType}
        ,assert_content =  #{assertContent}
		where id = #{id}
